fishes = ''
max = 0
maxFishes = ''

inputs.eachWithIndex { input1, i1 ->
    inputs.eachWithIndex { input2, i2 ->
        if (i1 == i2) {
            return
        }
        fishes = "[${input1},${input2}]"

        while(reduce('explode') || reduce('split')) {}

        def mag = cal(fishes)
        max = Math.max(max, mag)
        if (mag == max) {
            maxFishes = "${input1} + ${input2} = ${fishes}"
        }
    }
}

